templates.api-route:
import { {name}Handler } from './handler';

export async function GET(request: Request) {
  return {name}Handler.get(request);
}

export async function POST(request: Request) {
  return {name}Handler.post(request);
}

templates.api-handler:
export const {name}Handler = {
  async get(request: Request): Promise<Response> {
    return Response.json({ message: 'GET {name}' });
  },
  async post(request: Request): Promise<Response> {
    return Response.json({ message: 'POST {name}' });
  }
};
